export class CDN {
    constructor(options) {
        Object.defineProperty(this, "options", {
            enumerable: true,
            configurable: true,
            writable: true,
            value: options
        });
    }
    defaultAvatar(id) {
        return `${this.options.api}/users/${id}/default_avatar`;
    }
    avatar(hash, filename, size = 1024) {
        return `${this.options.cdn}/avatars/${hash}/${filename}?max_side=${size}`;
    }
    icon(hash, size = 1024) {
        return `${this.options.cdn}/icons/${hash}?max_side=${size}`;
    }
    invite(code) {
        return `${this.options.app}/invite/${code}`;
    }
    banner(hash, size = 1024) {
        return `${this.options.cdn}/banners/${hash}?max_side=${size}`;
    }
}
