import { User } from './User.js';
export class ClientUser extends User {
    constructor() {
        super(...arguments);
        Object.defineProperty(this, "notes", {
            enumerable: true,
            configurable: true,
            writable: true,
            value: null
        });
    }
    async setUsername(username, password) {
        if (this.bot && password) {
            throw new Error('Bots did not have passwords');
        }
        await this.client.api.patch('/users/@me/username', {
            body: { username, password },
        });
    }
    async setStatus(text, presence) {
        await this.client.api.patch('/users/@me', {
            body: { status: { text, presence } },
        });
    }
}
